import React, { useState, useEffect } from 'react';
import './App.css';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import Layout from './components/Layout/Layout';
import Dashboard from './components/Dashboard/Dashboard';
import authAPI from './api/auth';

function App() {
  const [currentPage, setCurrentPage] = useState('login');
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Vérifier l'authentification au chargement
  useEffect(() => {
    const checkAuth = () => {
      if (authAPI.isAuthenticated()) {
        const currentUser = authAPI.getCurrentUser();
        setUser(currentUser);
        // Rediriger vers la page par défaut selon le rôle
        const defaultPage = getDefaultPageForRole(currentUser?.role);
        setCurrentPage(defaultPage);
      }
      setLoading(false);
    };

    checkAuth();
  }, []);

  // Fonction pour obtenir la page par défaut selon le rôle
  const getDefaultPageForRole = (role) => {
    const defaultPages = {
      'chef-projet': 'dashboard',
      'conducteur-travaux': 'vue-chantier',
      'chef-chantier': 'mon-espace',
      'chef-equipe': 'tableau-bord',
      'fournisseur': 'mes-commandes'
    };
    return defaultPages[role] || 'mon-espace';
  };

  const handleLogout = async () => {
    try {
      await authAPI.logout();
      setUser(null);
      setCurrentPage('login');
    } catch (error) {
      console.error('Erreur lors de la déconnexion:', error);
      authAPI.clearAuth();
      setUser(null);
      setCurrentPage('login');
    }
  };

  const handleNavigation = (page) => {
    if (page === 'logout') {
      handleLogout();
    } else {
      setCurrentPage(page);
    }
  };

  const renderPage = () => {
    if (loading) {
      return (
        <div className="loading-page">
          <div className="loading-spinner"></div>
          <p>Chargement...</p>
        </div>
      );
    }

    switch (currentPage) {
      case 'login':
        return <Login onNavigate={setCurrentPage} />;
      case 'register':
        return <Register onNavigate={setCurrentPage} />;
      default:
        // Toutes les autres pages utilisent le Layout avec Sidebar
        return (
          <Layout 
            user={user} 
            currentPage={currentPage} 
            onNavigate={handleNavigation}
          >
            <Dashboard user={user} currentPage={currentPage} />
          </Layout>
        );
    }
  };

  return (
    <div className="app">
      {renderPage()}
    </div>
  );
}

export default App;
