import React, { useEffect, useState, useCallback } from 'react';
import { getChantiers, createChantier, updateChantier, deleteChantier } from '../../../api/chefProjet';
import './Chantiers.css';
import { toast } from 'react-toastify';
import { FaEdit, FaTrash, FaPlus, FaSpinner, FaEye, FaTimes } from 'react-icons/fa';
import ChantierForm from './ChantierForm';

export default function Chantiers({ user }) {
  const [chantiers, setChantiers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(false);
  const [selectedLocation, setSelectedLocation] = useState([14.7167, -17.4677]); // Dakar par d√©faut
  const [useMap, setUseMap] = useState(true);
  const [address, setAddress] = useState('');
  const [form, setForm] = useState({
    id: null,
    nom: '',
    proprietaireId: '', // Chang√© de proprietaireEmail/proprietaireTelephone √† proprietaireId
    proprietaireType: 'email' // Pour compatibilit√© avec ChantierForm
  });
  const [showClientModal, setShowClientModal] = useState(false);
  const [selectedClient, setSelectedClient] = useState(null);

  const fetchData = async () => {
    setLoading(true);
    try {
      const data = await getChantiers();
      const normalizedChantiers = (data.chantiers || []).map(chantier => ({
        ...chantier,
        geolocalisation: typeof chantier.geolocalisation === 'string'
          ? { address: chantier.geolocalisation }
          : (chantier.geolocalisation || { address: 'Non sp√©cifi√©' })
      }));
      setChantiers(normalizedChantiers);
    } catch (e) {
      toast.error('Erreur lors du chargement des chantiers ‚ùå');
      setChantiers([]);
    }
    setLoading(false);
  };

  useEffect(() => { fetchData(); }, []);

  const handleSubmit = async () => {
    try {
      const geolocalisation = { address: address || 'Adresse non sp√©cifi√©e' };
      if (form.id) {
        await updateChantier(form.id, {
          nom: form.nom,
          geolocalisation,
          proprietaireId: form.proprietaireId // Utilisation de proprietaireId
        });
        toast.success('Chantier modifi√© avec succ√®s ‚úèÔ∏è');
        setShowEditForm(false);
      } else {
        await createChantier({
          nom: form.nom,
          geolocalisation,
          proprietaireId: form.proprietaireId // Utilisation de proprietaireId
        });
        toast.success('Chantier ajout√© avec succ√®s ‚úÖ');
        setShowCreateForm(false);
      }
      setForm({ id: null, nom: '', proprietaireId: '', proprietaireType: 'email' });
      setAddress('');
      setUseMap(true);
      fetchData();
    } catch (err) {
      toast.error('Erreur lors de la soumission ‚ùå');
    }
  };

  const handleEdit = (chantier) => {
    setForm({
      id: chantier.id,
      nom: chantier.nom || '',
      proprietaireId: chantier.proprietaireId || '' // Utilisation de proprietaireId
    });
    setAddress(chantier.geolocalisation?.address || '');
    setShowEditForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('√ätes-vous s√ªr de vouloir supprimer ce chantier ?')) {
      try {
        await deleteChantier(id);
        toast.success('Chantier supprim√© üóëÔ∏è');
        fetchData();
      } catch (err) {
        toast.error('Erreur lors de la suppression ‚ùå');
      }
    }
  };

  const handleMapClick = useCallback((e) => {
    const newLocation = [e.latlng.lat, e.latlng.lng];
    setSelectedLocation(newLocation);
    setAddress(`Lat: ${newLocation[0].toFixed(6)}, Lng: ${newLocation[1].toFixed(6)}`);
  }, []);

  const handleFormSubmit = useCallback(() => {
    handleSubmit();
  }, [form, selectedLocation, useMap, address]);

  const handleFormClose = useCallback(() => {
    setShowCreateForm(false);
  }, []);

  const handleViewProprietaire = useCallback(async (proprietaireId) => {
    try {
      const response = await fetch(`/api/chef-projet/clients?search=${encodeURIComponent(proprietaireId)}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('firebaseToken') || localStorage.getItem('authToken') || localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        const client = data.users?.find(user => 
          user.id === proprietaireId // Recherche par ID au lieu de email/t√©l√©phone
        );

        if (client) {
          setSelectedClient(client);
          setShowClientModal(true);
        } else {
          toast.error('Client non trouv√© dans la base de donn√©es');
        }
      } else {
        toast.error('Erreur lors de la recherche du client');
      }
    } catch (error) {
      console.error('Erreur:', error);
      toast.error('Erreur lors de la consultation du propri√©taire');
    }
  }, []);

  const EditChantierForm = () => (
    <div className="modal-form">
      <form className="chantier-form" onSubmit={(e) => { e.preventDefault(); handleSubmit(); }}>
        <input placeholder="Nom" value={form.nom} onChange={(e) => setForm({ ...form, nom: e.target.value })} required />
        <input placeholder="Adresse" value={address} onChange={(e) => setAddress(e.target.value)} required />
        <input placeholder="ID du propri√©taire" value={form.proprietaireId} onChange={(e) => setForm({ ...form, proprietaireId: e.target.value })} required />
        <button type="submit">Modifier</button>
        <button type="button" onClick={() => setShowEditForm(false)}>Annuler</button>
      </form>
