import React, { useEffect, useState, useCallback } from 'react';
import { getChantiers, createChantier, updateChantier, deleteChantier } from '../../../api/chefProjet';
import './Chantiers.css';
import { toast } from 'react-toastify';
import { FaEdit, FaTrash, FaPlus, FaSpinner, FaTimes, FaSearch, FaMapMarkerAlt } from 'react-icons/fa';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Correction pour les icônes Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

export default function Chantiers({ user }) {
  // Optimisation pour éviter les re-renders inutiles
  const memoizedForm = React.useMemo(() => form, [form.nom, form.proprietaireEmail, form.proprietaireTelephone, form.proprietaireType]);
  const [chantiers, setChantiers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showEditForm, setShowEditForm] = useState(false);
  const [selectedLocation, setSelectedLocation] = useState([14.7167, -17.4677]); // Dakar par défaut
  const [useMap, setUseMap] = useState(true);
  const [address, setAddress] = useState('');
  const [form, setForm] = useState({
    id: null,
    nom: '',
    proprietaireEmail: '',
    proprietaireTelephone: '',
    proprietaireType: 'email' // 'email' ou 'telephone'
  });

  const fetchData = async () => {
    setLoading(true);
    try {
      const data = await getChantiers();
      setChantiers(data.chantiers || []);
    } catch (e) {
      toast.error('Erreur lors du chargement des chantiers ❌');
      setChantiers([]);
    }
    setLoading(false);
  };

  useEffect(() => { fetchData(); }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (form.id) {
        await updateChantier(form.id, {
          nom: form.nom,
          geolocalisation: `${selectedLocation[0]}, ${selectedLocation[1]}`,
          proprietaireEmailOrTel: form.proprietaireEmail || form.proprietaireTelephone
        });
        toast.success('Chantier modifié avec succès ✏️');
        setShowEditForm(false);
      } else {
        await createChantier({
          nom: form.nom,
          geolocalisation: useMap ? `${selectedLocation[0]}, ${selectedLocation[1]}` : address,
          proprietaireEmailOrTel: form.proprietaireType === 'email' ? form.proprietaireEmail : form.proprietaireTelephone
        });
        toast.success('Chantier ajouté avec succès ✅');
        setShowCreateForm(false);
      }
      setForm({ id: null, nom: '', proprietaireEmail: '', proprietaireTelephone: '', proprietaireType: 'email' });
      setAddress('');
      setUseMap(true);
      fetchData();
    } catch (err) {
      toast.error('Erreur lors de la soumission ❌');
    }
  };

  const handleEdit = (chantier) => {
    setForm({
      id: chantier.id,
      nom: chantier.nom || '',
      proprietaireEmail: chantier.proprietaireEmailOrTel || '',
      proprietaireTelephone: chantier.proprietaireEmailOrTel || ''
    });
    setShowEditForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce chantier ?')) {
      try {
        await deleteChantier(id);
        toast.success('Chantier supprimé 🗑️');
        fetchData();
      } catch (err) {
        toast.error('Erreur lors de la suppression ❌');
      }
    }
  };

  const handleMapClick = (e) => {
    const newLocation = [e.latlng.lat, e.latlng.lng];
    setSelectedLocation(newLocation);
    // Ici on pourrait faire un reverse geocoding pour obtenir l'adresse
    setAddress(`Lat: ${newLocation[0].toFixed(6)}, Lng: ${newLocation[1].toFixed(6)}`);
  };

  const NouveauChantierForm = () => (
    <div className="nouveau-chantier-overlay">
      <div className="nouveau-chantier-container">
        <div className="nouveau-chantier-header">
          <h2>NOUVEAU PROJET</h2>
          <button 
            className="close-btn" 
            onClick={() => setShowCreateForm(false)}
          >
            <FaTimes />
          </button>
        </div>
        
        <div className="nouveau-chantier-content">
          <div className="form-section">
            <form onSubmit={handleSubmit} className="chantier-form">
              <div className="form-group">
                <label>Nom du Chantier *</label>
                <input 
                  type="text" 
                  value={form.nom} 
                  onChange={(e) => setForm({ ...form, nom: e.target.value })} 
                  placeholder="Ex: SOTRAC DISTRIBUTION"
                  required 
                />
              </div>
              
              <div className="form-group">
                <label>Type de Contact Propriétaire</label>
                <div className="contact-type-selector">
                  <label className="radio-label">
                    <input 
                      type="radio" 
                      name="contactType" 
                      value="email"
                      checked={form.proprietaireType === 'email'}
                      onChange={(e) => setForm({ ...form, proprietaireType: e.target.value })}
                    />
                    Email
                  </label>
                  <label className="radio-label">
                    <input 
                      type="radio" 
                      name="contactType" 
                      value="telephone"
                      checked={form.proprietaireType === 'telephone'}
                      onChange={(e) => setForm({ ...form, proprietaireType: e.target.value })}
                    />
                    Téléphone
                  </label>
                </div>
              </div>
              
              {form.proprietaireType === 'email' ? (
                <div className="form-group">
                  <label>Email Propriétaire</label>
                  <input 
                    type="email" 
                    value={form.proprietaireEmail} 
                    onChange={(e) => setForm({ ...form, proprietaireEmail: e.target.value })} 
                    placeholder="proprietaire@email.com"
                  />
                </div>
              ) : (
                <div className="form-group">
                  <label>Téléphone Propriétaire</label>
                  <input 
                    type="tel" 
                    value={form.proprietaireTelephone} 
                    onChange={(e) => setForm({ ...form, proprietaireTelephone: e.target.value })} 
                    placeholder="+221 77 123 45 67"
                  />
                </div>
                             )}
               
               <div className="form-group">
                 <label>Localisation du Chantier</label>
                 <div className="location-type-selector">
                   <label className="radio-label">
                     <input 
                       type="radio" 
                       name="locationType" 
                       value="map"
                       checked={useMap}
                       onChange={() => setUseMap(true)}
                     />
                     Utiliser la carte
                   </label>
                   <label className="radio-label">
                     <input 
                       type="radio" 
                       name="locationType" 
                       value="manual"
                       checked={!useMap}
                       onChange={() => setUseMap(false)}
                     />
                     Saisie manuelle
                   </label>
                 </div>
               </div>
               
               {useMap ? (
                 <div className="form-group">
                   <label>Emplacement sélectionné</label>
                   <input 
                     type="text" 
                     value={address || `Lat: ${selectedLocation[0].toFixed(6)}, Lng: ${selectedLocation[1].toFixed(6)}`}
                     readOnly
                     placeholder="Cliquez sur la carte pour sélectionner l'emplacement"
                     className="location-display"
                   />
                 </div>
               ) : (
                 <div className="form-group">
                   <label>Adresse du chantier</label>
                   <input 
                     type="text" 
                     value={address}
                     onChange={(e) => setAddress(e.target.value)}
                     placeholder="Ex: Rue 10, Dakar, Sénégal"
                   />
                 </div>
               )}
               
               <button type="submit" className="create-btn">
                 Créer le Chantier
               </button>
             </form>
           </div>
          
                     {useMap && (
             <div className="map-section">
               <div className="map-header">
                 <div className="search-container">
                   <FaSearch className="search-icon" />
                   <input 
                     type="text" 
                     placeholder="Rechercher un lieu (ex: Dakar, Sénégal)" 
                     className="search-input"
                     onKeyPress={(e) => {
                       if (e.key === 'Enter') {
                         // Ici on pourrait intégrer une API de géocodage
                         // Pour l'instant, on affiche juste un message
                         toast.info('Fonctionnalité de recherche en cours de développement');
                       }
                     }}
                   />
                 </div>
                 <div className="map-info">
                   <p><strong>Instructions :</strong></p>
                   <ul>
                     <li>Cliquez sur la carte pour sélectionner l'emplacement du chantier</li>
                     <li>Le marqueur jaune indique l'emplacement sélectionné</li>
                     <li>Vous pouvez zoomer/dézoomer avec les boutons + et -</li>
                   </ul>
                 </div>
               </div>
               
               <div className="map-container">
                 <MapContainer 
                   center={selectedLocation} 
                   zoom={13} 
                   style={{ height: '400px', width: '100%' }}
                   onClick={handleMapClick}
                   key={`map-${selectedLocation[0]}-${selectedLocation[1]}`}
                 >
                   <TileLayer
                     url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                     attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                   />
                   <Marker position={selectedLocation}>
                     <Popup>
                       <div>
                         <FaMapMarkerAlt style={{ color: '#f5b942' }} />
                         <strong>Emplacement du Chantier</strong>
                         <br />
                         Latitude: {selectedLocation[0].toFixed(6)}
                         <br />
                         Longitude: {selectedLocation[1].toFixed(6)}
                       </div>
                     </Popup>
                   </Marker>
                 </MapContainer>
               </div>
             </div>
           )}
        </div>
      </div>
    </div>
  );

  const EditChantierForm = () => (
    <div className="modal-form">
      <form className="chantier-form" onSubmit={handleSubmit}>
        <input placeholder="Nom" value={form.nom} onChange={(e) => setForm({ ...form, nom: e.target.value })} required />
        <input placeholder="Email ou téléphone du client" value={form.proprietaireEmail} onChange={(e) => setForm({ ...form, proprietaireEmail: e.target.value })} required />
        <button type="submit">Modifier</button>
        <button type="button" onClick={() => setShowEditForm(false)}>Annuler</button>
      </form>
    </div>
  );

  return (
    <div className="chantiers-container">
      <h3>Gestion des chantiers</h3>

      <div className="header-actions">
        <button className="btn-ajouter" onClick={() => {
          setShowCreateForm(true);
          setForm({ id: null, nom: '', proprietaireEmail: '', proprietaireTelephone: '', proprietaireType: 'email' });
          setAddress('');
          setUseMap(true);
        }}>
          <FaPlus /> Ajouter un chantier
        </button>
      </div>

      {showCreateForm && <NouveauChantierForm />}
      {showEditForm && <EditChantierForm />}

      {loading ? (
        <div className="loader">
          <FaSpinner className="spin" size={30} />
          Chargement des chantiers...
        </div>
      ) : (
        <table className="chantiers-table">
          <thead>
            <tr>
              <th>Nom</th>
              <th>Géolocalisation</th>
              <th>Propriétaire</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {chantiers.map((chantier) => (
              <tr key={chantier.id}>
                <td>{chantier.nom}</td>
                <td>{chantier.geolocalisation}</td>
                <td>{chantier.proprietaireEmailOrTel || 'Non défini'}</td>
                <td>
                  <button className="icon-btn" onClick={() => handleEdit(chantier)}><FaEdit /></button>
                  <button className="icon-btn" onClick={() => handleDelete(chantier.id)}><FaTrash /></button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
