import React, { useEffect, useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';

import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import Accueil from './components/Accueil';
import DashboardChefProjet from './components/Dashboard/ChefProjet/DashboardChefProjet';
import DashboardConducteurTravaux from './components/Dashboard/ConducteurTravaux/DashboardConducteurTravaux';
import DashboardChefChantier from './components/Dashboard/ChefChantier/DashboardChefChantier';
import DashboardChefEquipe from './components/Dashboard/ChefEquipe/DashboardChefEquipe';
import DashboardFournisseur from './components/Dashboard/Fournisseur/DashboardFournisseur';
// import DashboardClient from './components/Dashboard/Client/DashboardClient.jsx';

import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    try {
      const raw = localStorage.getItem('user');
      if (token && raw) {
        setUser(JSON.parse(raw));
      }
    } catch (e) {
      console.warn('User localStorage parse error:', e);
    }
  }, []);

  const DashboardWrapper = ({ user }) => {
    if (!user) return <Navigate to="/login" />;

    const role = user?.role?.toLowerCase().replace(/\s/g, '');

    switch (role) {
      case 'chef-projet':
      case 'chefdeprojet':
        return <DashboardChefProjet user={user} />;
      case 'conducteur-travaux':
      case 'conducteurdestravaux':
        return <DashboardConducteurTravaux user={user} />;
      case 'chef-chantier':
      case 'chefdechantier':
        return <DashboardChefChantier user={user} />;
      case 'chef-equipe':
      case 'chefdequipe':
        return <DashboardChefEquipe user={user} />;
      case 'fournisseur':
        return <DashboardFournisseur user={user} />;
      // case 'client':
      //   return <DashboardClient user={user} />;
      default:
        return <div>RÃ´le inconnu</div>;
    }
  };

  return (
    <>
      <ToastContainer position="top-right" autoClose={3000} />
      <Routes>
        <Route path="/" element={<Accueil />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/dashboard/*" element={<DashboardWrapper user={user} />} />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </>
  );
}

export default App;
