import React, { useState } from 'react';
import Sidebar from '../../Layout/Sidebar';
import ProfilChefProjet from './ProfilChefProjet';
import Chantiers from './Chantiers';
import CommandeChefProjet from './CommandeChefProjet';
import './DashboardChefProjet.css';

const COMPONENTS = {
  dashboard: () => <div>Bienvenue sur le Dashboard Chef de Projet</div>,
  profil: ProfilChefProjet,
  chantiers: Chantiers,
  'magasin/commande': CommandeChefProjet, 
};

export default function DashboardChefProjet({ user }) {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const CurrentComponent = COMPONENTS[currentPage] || COMPONENTS.dashboard;

  return (
    <div className="dashboard-role" style={{ display: 'flex' }}>
      <Sidebar user={user} currentPage={currentPage} onNavigate={setCurrentPage} />
      <div style={{ flex: 1, padding: '32px' }}>
        <CurrentComponent user={user} />
      </div>
    </div>
  );
}
