import React, { useEffect, useState } from 'react';
import chefChantierAPI from '../../../api/chefChantier';
import './PointageOuvriers.css'; 

// Composant pour g√©rer le pointage des ouvriers
export default function PointageOuvriers() {
  const [ouvriers, setOuvriers] = useState([]);
  const [pointages, setPointages] = useState([]);
  const [loading, setLoading] = useState(false);

  // CRUD form state
  const [form, setForm] = useState({ nom: '', metier: '' });
  const [editId, setEditId] = useState(null);

  // Charger ouvriers et pointages
  const fetchData = async () => {
    setLoading(true);
    try {
      const ouvriersRes = await chefChantierAPI.getOuvriers();
      const pointagesRes = await chefChantierAPI.getPointages();
      setOuvriers(ouvriersRes.ouvriers || []);
      setPointages(pointagesRes.pointages || []);
    } catch (e) {
      setOuvriers([]);
      setPointages([]);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  // CRUD ouvriers
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (editId) {
      await chefChantierAPI.updateOuvrier(editId, form);
    } else {
      await chefChantierAPI.createOuvrier(form);
    }
    setForm({ nom: '', metier: '' });
    setEditId(null);
    fetchData();
  };

  const handleEdit = (ouvrier) => {
    setForm({ nom: ouvrier.nom, metier: ouvrier.metier });
    setEditId(ouvrier.id);
  };

  const handleDelete = async (id) => {
    await chefChantierAPI.deleteOuvrier(id);
    fetchData();
  };

  // Pointer un ouvrier
  const handlePointer = async (ouvrierId) => {
    await chefChantierAPI.pointerOuvrier({ ouvrierId });
    fetchData();
  };

  // V√©rifier si l'ouvrier est d√©j√† point√© aujourd'hui
  const isOuvrierPointe = (ouvrierId) => {
    const today = new Date().toISOString().slice(0, 10);
    return pointages.some(
      (p) =>
        p.ouvrierId === ouvrierId &&
        p.present &&
        p.date &&
        p.date.slice(0, 10) === today
    );
  };

  return (
    <div className="pointage-container">
      <h3>Gestion des ouvriers</h3>
      {/* CRUD Form */}
      <form className="ouvrier-form" onSubmit={handleSubmit} style={{ marginBottom: 16 }}>
        <input
          placeholder="Nom"
          value={form.nom}
          onChange={e => setForm({ ...form, nom: e.target.value })}
          required
        />
        <input
          placeholder="M√©tier"
          value={form.metier}
          onChange={e => setForm({ ...form, metier: e.target.value })}
          required
        />
        <button type="submit">{editId ? 'Modifier' : 'Ajouter'}</button>
        {editId && (
          <button type="button" onClick={() => { setEditId(null); setForm({ nom: '', metier: '' }); }}>
            Annuler
          </button>
        )}
      </form>

      {/* Liste ouvriers + actions CRUD + pointage */}
      <h3>Pointage des ouvriers</h3>
      {loading && <div>Chargement...</div>}
      <ul className="ouvriers-list">
        {ouvriers.map((o) => (
          <li key={o.id}>
            {o.nom} ({o.metier}){' '}
            {isOuvrierPointe(o.id) ? (
              <span className='badge-present'>Pr√©sent</span>
            ) : (
              <button onClick={() => handlePointer(o.id)}>Pointer</button>
            )}
            <button onClick={() => handleEdit(o)}>‚úèÔ∏è</button>
            <button onClick={() => handleDelete(o.id)}>üóëÔ∏è</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
