        </div>
      ) : (
        <table className="chantiers-table">
          <thead>
            <tr>
              <th>Nom</th>
              <th>G√©olocalisation</th>
              <th>Propri√©taire</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {chantiers.map((chantier) => (
              <tr key={chantier.id}>
                <td>{chantier.nom || 'Non d√©fini'}</td>
                <td>{chantier.geolocalisation?.address || 'Non sp√©cifi√©'}</td>
                <td>
                  {chantier.proprietaireNom}
