import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Auth.css';
import logo from '../../assets/logo.png';
import planImage from '../../assets/plan.jpg';
import authAPI from '../../api/auth';
import { auth, GoogleAuthProvider, signInWithPopup } from '../../api/firebaseClient';

function Register() {
  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    telephone: '',
    adresse: '',
    email: '',
    motDePasse: '',
    confirmerMotDePasse: '',
    role: 'chef-projet' // Rôle forcé
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [errors, setErrors] = useState([]);

  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setErrors([]);

    if (formData.motDePasse !== formData.confirmerMotDePasse) {
      setError('Les mots de passe ne correspondent pas');
      setLoading(false);
      return;
    }

    try {
      const response = await authAPI.register(formData);
      if (response.success) {
        navigate('/login');
      } else if (response.errors?.length) {
        setErrors(response.errors.map(err => err.msg));
        setError(response.message || 'Erreur lors de l\'inscription');
      } else if (response.message) {
        setError(response.message);
      } else {
        setError('Erreur lors de l\'inscription');
      }
    } catch (error) {
      if (error.response?.data?.errors) {
        setErrors(error.response.data.errors.map(err => err.msg));
        setError(error.response.data.message);
      } else {
        setError(error.message || 'Erreur lors de l\'inscription');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleRegister = async () => {
    setLoading(true);
    setError('');
    try {
      const provider = new GoogleAuthProvider();
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      const response = await authAPI.loginWithSocial({
        email: user.email,
        nom: user.displayName?.split(' ').slice(-1)[0] || '',
        prenom: user.displayName?.split(' ')[0] || '',
        provider: 'google',
        uid: user.uid
      });

      if (response.success) {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data));
        navigate('/dashboard');
      } else {
        setError(response.message || "Erreur lors de l'inscription Google.");
      }
    } catch (error) {
      setError(error.message || "Erreur Google.");
    }
    setLoading(false);
  };

  return (
    <div className="auth-container">
      <div className="form-section">
        <div className="form-content">
          <div className="logo-section">
            <img src={logo} alt="E-TABAX Logo" className="logo" />
            <p className="tagline">TABAX SUNU REW</p>
          </div>

          <form onSubmit={handleSubmit} className="auth-form">
            {['nom', 'prenom', 'telephone', 'adresse', 'email'].map((field) => (
              <div className="form-group" key={field}>
                <label htmlFor={field}>{field.charAt(0).toUpperCase() + field.slice(1)}</label>
                <input
                  type={field === 'email' ? 'email' : 'text'}
                  id={field}
                  name={field}
                  value={formData[field]}
                  onChange={handleInputChange}
                  required
                />
              </div>
            ))}

            <div className="form-group">
              <label htmlFor="motDePasse">Mot de passe</label>
              <input
                type="password"
                id="motDePasse"
                name="motDePasse"
                value={formData.motDePasse}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="confirmerMotDePasse">Confirmer le mot de passe</label>
              <input
                type="password"
                id="confirmerMotDePasse"
                name="confirmerMotDePasse"
                value={formData.confirmerMotDePasse}
                onChange={handleInputChange}
                required
              />
            </div>

            {error && <div className="error-message">{error}</div>}
            {errors.length > 0 && (
              <ul className="error-message" style={{ marginTop: '-10px' }}>
                {errors.map((err, idx) => <li key={idx}>{err}</li>)}
              </ul>
            )}
            <button type="submit" className="auth-button" disabled={loading}>
              {loading ? 'Inscription...' : 'S\'inscrire'}
            </button>
          </form>

          <div className="separator">
            <span>Ou</span>
          </div>

          <div className="social-buttons">
            <button className="social-button google" type="button" onClick={handleGoogleRegister} disabled={loading}>
              {/* SVG Google ici */}
              S'inscrire via Google
            </button>

            <button className="social-button apple" disabled>
              {/* SVG Apple ici */}
              S'inscrire via Apple
            </button>
          </div>

          <div className="auth-link">
            <span>Vous avez déjà un compte? </span>
            <button type="button" className="link" onClick={() => navigate('/login')}>
              Connectez-vous
            </button>
          </div>
        </div>
      </div>

      <div className="image-section">
        <div className="plan-image-container">
          <img src={planImage} alt="Plan de construction" className="plan-image" />
        </div>
      </div>
    </div>
  );
}

export default Register;
