import React, { useState, useEffect } from 'react';
import './App.css';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import authAPI from './api/auth';

function App() {
  const [currentPage, setCurrentPage] = useState('login'); // 'login', 'register', 'home'
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Vérifier l'authentification au chargement
  useEffect(() => {
    const checkAuth = () => {
      if (authAPI.isAuthenticated()) {
        const currentUser = authAPI.getCurrentUser();
        setUser(currentUser);
        setCurrentPage('home');
      }
      setLoading(false);
    };

    checkAuth();
  }, []);

  const handleLogout = async () => {
    try {
      await authAPI.logout();
      setUser(null);
      setCurrentPage('login');
    } catch (error) {
      console.error('Erreur lors de la déconnexion:', error);
      // Forcer la déconnexion même en cas d'erreur
      authAPI.clearAuth();
      setUser(null);
      setCurrentPage('login');
    }
  };

  const renderPage = () => {
    if (loading) {
      return (
        <div className="loading-page">
          <div className="loading-spinner"></div>
          <p>Chargement...</p>
        </div>
      );
    }

    switch (currentPage) {
      case 'login':
        return <Login onNavigate={setCurrentPage} />;
      case 'register':
        return <Register onNavigate={setCurrentPage} />;
      case 'home':
        return (
          <div className="home-page">
            <header className="home-header">
              <h1>Bienvenue sur E-TABAX</h1>
              {user && (
                <div className="user-info">
                  <span>Bonjour, {user.prenom} {user.nom}</span>
                  <button onClick={handleLogout} className="logout-button">
                    Se déconnecter
                  </button>
                </div>
              )}
            </header>
            <main className="home-content">
              <p>Page d'accueil - En cours de développement</p>
              <div className="home-actions">
                <button onClick={() => setCurrentPage('login')} className="home-button">
                  Se connecter
                </button>
              </div>
            </main>
          </div>
        );
      default:
        return <Login onNavigate={setCurrentPage} />;
    }
  };

  return (
    <div className="app">
      {renderPage()}
    </div>
  );
}

export default App;
