import React from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/logo.png';

const roles = [
  {
    key: 'client',
    label: 'PARTICULIER',
    description: 'La Plateforme idéale pour avoir une main mise sur son chantier, ces besoins en approviossnement et le personnel.',
    color: '#FFD966',
    icon: '👤'
  },
  {
    key: 'fournisseur',
    label: 'FOURNISSEUR',
    description: 'Vous voulez un espace adapté pour vendre vos produits et gérer les commandes et livraison ? Vous êtes au bon endroit!.',
    color: '#F4B400',
    icon: '🏗️'
  },
  {
    key: 'chef-projet',
    label: 'CHEF DE PROJET',
    description: 'Si vous voulez administrer tout un chantier sans problème ou si vous souhaitez vous faire connaître dans le milieu inscrivez-vous comme chef de projet.',
    color: '#B6D7A8',
    icon: '🧑‍💼'
  }
];

function Accueil({ onNavigate }) {

  const handleRoleClick = (role) => {
    onNavigate('register');
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: '#f7f7f7',
      display: 'flex',
      flexDirection: 'column'
    }}>
      <header style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '32px 48px 0 48px'
      }}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img src={logo} alt="E-TABAX Logo" style={{ height: 48, marginRight: 16 }} />
          <span style={{ fontWeight: 'bold', fontSize: 18, color: '#333' }}>E TABAX.</span>
        </div>
        <button
          style={{
            background: '#222',
            color: '#fff',
            border: 'none',
            borderRadius: 6,
            padding: '10px 24px',
            fontWeight: 'bold',
            cursor: 'pointer'
          }}
          onClick={() => navigate('/login')}
        >
          Connexion
        </button>
      </header>

      <main style={{
        flex: 1,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        gap: 48
      }}>
        <div>
          <h1 style={{
            fontSize: 36,
            fontWeight: 'bold',
            letterSpacing: '0.2em',
            marginBottom: 32,
            color: '#222'
          }}>
            TABAX SUNU REW
          </h1>
          <div style={{ display: 'flex', flexDirection: 'column', gap: 24 }}>
            {roles.map(role => (
              <div
                key={role.key}
                onClick={() => handleRoleClick(role.key)}
                style={{
                  background: role.color,
                  borderRadius: 16,
                  padding: '24px 32px',
                  boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  transition: 'transform 0.15s',
                  border: '2px solid #eee'
                }}
                onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.03)'}
                onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}
              >
                <span style={{
                  fontSize: 36,
                  marginRight: 24
                }}>{role.icon}</span>
                <div>
                  <div style={{ fontWeight: 'bold', fontSize: 20, marginBottom: 8 }}>{role.label}</div>
                  <div style={{ color: '#444', fontSize: 15 }}>{role.description}</div>
                </div>
                <span style={{ marginLeft: 'auto', fontSize: 28, color: '#222' }}>▶</span>
              </div>
            ))}
          </div>
        </div>
        <div>
          <img
            src={require('../assets/plan.jpg')}
            alt="Plan de construction"
            style={{
              width: 340,
              borderRadius: 24,
              boxShadow: '0 4px 24px rgba(0,0,0,0.13)'
            }}
          />
        </div>
      </main>
    </div>
  );
}

export default Accueil;
