import React, { useState } from 'react';
import './App.css';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';

function App() {
  const [currentPage, setCurrentPage] = useState('login'); // 'login', 'register', 'home'

  const renderPage = () => {
    switch (currentPage) {
      case 'login':
        return <Login onNavigate={setCurrentPage} />;
      case 'register':
        return <Register onNavigate={setCurrentPage} />;
      case 'home':
        return (
          <div className="home-page">
            <h1>Bienvenue sur E-TABAX</h1>
            <p>Page d'accueil - En cours de d√©veloppement</p>
            <button onClick={() => setCurrentPage('login')}>Se connecter</button>
          </div>
        );
      default:
        return <Login onNavigate={setCurrentPage} />;
    }
  };

  return (
    <div className="app">
      {renderPage()}
    </div>
  );
}

export default App;
