import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Auth.css';
import logo from '../../assets/logo.png';
import planImage from '../../assets/plan.jpg';
import authAPI from '../../api/auth';
import { auth, GoogleAuthProvider, signInWithPopup } from '../../api/firebaseClient';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    motDePasse: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await authAPI.login(formData);
      if (response.success) {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data));
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Erreur de connexion:', error);
      setError(error.message || 'Erreur lors de la connexion');
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleLogin = async () => {
    setLoading(true);
    setError('');
    try {
      const provider = new GoogleAuthProvider();
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      const response = await authAPI.loginWithSocial({
        email: user.email,
        nom: user.displayName?.split(' ').slice(-1)[0] || '',
        prenom: user.displayName?.split(' ')[0] || '',
        provider: 'google',
        uid: user.uid
      });

      if (response.success) {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data));
        navigate('/dashboard');
      } else {
        setError(response.message || "Erreur lors de la connexion Google.");
      }
    } catch (error) {
      setError(error.message || "Erreur Google.");
    }
    setLoading(false);
  };

  const handleAppleLogin = async () => {
    setLoading(true);
    setError('');
    try {
      setError("Connexion Apple non configurÃ©e.");
    } catch (error) {
      setError(error.message || "Erreur Apple.");
    }
    setLoading(false);
  };

  return (
    <div className="auth-container">
      <div className="form-section">
        <div className="form-content">
          <button
            type="button"
            onClick={() => navigate('/')}
            style={{
              background: 'none',
              border: 'none',
              cursor: 'pointer',
              marginBottom: 10,
              fontSize: 24,
              color: '#222'
            }}
            aria-label="Retour accueil"
          >
            â
          </button>

          <div className="logo-section">
            <img src={logo} alt="E-TABAX Logo" className="logo" />
            <p className="tagline">TABAX SUNU REW</p>
          </div>

          <form onSubmit={handleSubmit} className="auth-form">
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="example@email.com"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="motDePasse">Mot de passe</label>
              <input
                type="password"
                id="motDePasse"
                name="motDePasse"
                value={formData.motDePasse}
                onChange={handleInputChange}
                placeholder="â¢â¢â¢â¢â¢â¢"
                required
              />
              <a className="forgot-password">Mot de passe oubliÃ©?</a>
            </div>

            {error && <div className="error-message">{error}</div>}
            <button type="submit" className="auth-button" disabled={loading}>
              {loading ? 'Connexion...' : 'Se connecter'}
            </button>
          </form>

          <div className="separator">
            <span></span>
            <div className='ou'>OU</div>
          </div>

          <div className="social-buttons">
            <button className="social-button google" onClick={handleGoogleLogin} type="button">
              {/* SVG Google */}
              Se connecter via Google
            </button>

            <button className="social-button apple" onClick={handleAppleLogin} type="button">
              {/* SVG Apple */}
              Se connecter via Apple
            </button>
          </div>

          {/* <div className="auth-link">
            <span>Vous n'avez pas de compte? </span>
            <a className="link" onClick={() => navigate('/register')}>Inscrivez-vous</a>
          </div> */}
        </div>
      </div>

      <div className="image-section">
        <div className="plan-image-container">
          <img src={planImage} alt="Plan de construction" className="plan-image" />
        </div>
      </div>
    </div>
  );
}

export default Login;
