import React, { useEffect, useState } from 'react';

const weatherIcons = {
  'peu nuageux': 'https://openweathermap.org/img/wn/02d.png',
  'légère pluie': 'https://openweathermap.org/img/wn/10d.png',
  'couvert': 'https://openweathermap.org/img/wn/04d.png',
  'nuageux': 'https://openweathermap.org/img/wn/03d.png',
  // Ajoute d'autres descriptions si besoin
};

const MeteoPlanning = () => {
  const [meteoData, setMeteoData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('/api/meteo/dakar') // Remplace par ton URL API météo
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setMeteoData(data);
        } else {
          setError('Météo non disponible.');
        }
      })
      .catch(() => setError('Erreur lors du chargement de la météo.'))
      .finally(() => setLoading(false));
  }, []);

  if (loading) return <p>Chargement...</p>;
  if (error) return <p>{error}</p>;
  if (!meteoData) return null;

  const { city, weather, forecast } = meteoData;

  return (
    <div style={{ maxWidth: '600px', margin: 'auto', padding: '1rem', fontFamily: 'Arial, sans-serif' }}>
      <h2>Météo & Planning</h2>
      <h3>{city}</h3>

      {/* Météo actuelle */}
      {weather ? (
        <div style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '1rem' }}>
          <img
            src={weatherIcons[weather.description] || 'https://openweathermap.org/img/wn/01d.png'}
            alt={weather.description}
            style={{ width: '50px', height: '50px' }}
          />
          <div>
            <p><strong>Condition :</strong> {weather.description}</p>
            <p><strong>Température :</strong> {weather.temperature.toFixed(1)} °C</p>
            <p><strong>Humidité :</strong> {weather.humidity} %</p>
            <p><strong>Vent :</strong> {weather.wind_speed} km/h</p>
          </div>
        </div>
      ) : (
        <p>Météo non disponible.</p>
      )}

      {/* Prévisions à venir */}
      <h4>Prévisions météo à venir</h4>
      {forecast && forecast.length > 0 ? (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ backgroundColor: '#f5b942', color: 'white' }}>
              <th style={{ padding: '8px' }}>Date / Heure</th>
              <th style={{ padding: '8px' }}>Condition</th>
              <th style={{ padding: '8px' }}>Température (°C)</th>
              <th style={{ padding: '8px' }}>Humidité (%)</th>
              <th style={{ padding: '8px' }}>Vent (km/h)</th>
            </tr>
          </thead>
          <tbody>
            {forecast.map((item, idx) => (
              <tr key={idx} style={{ borderBottom: '1px solid #ddd' }}>
                <td style={{ padding: '8px' }}>{item.dt_txt}</td>
                <td style={{ padding: '8px', display: 'flex', alignItems: 'center', gap: '6px' }}>
                  <img
                    src={weatherIcons[item.description] || 'https://openweathermap.org/img/wn/01d.png'}
                    alt={item.description}
                    style={{ width: '30px', height: '30px' }}
                  />
                  {item.description}
                </td>
                <td style={{ padding: '8px' }}>{item.temperature.toFixed(1)}</td>
                <td style={{ padding: '8px' }}>{item.humidity}</td>
                <td style={{ padding: '8px' }}>{item.wind_speed.toFixed(1)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Prévisions non disponibles.</p>
      )}

      {/* Planning */}
      <h4>Planning de la journée</h4>
      <p>Consultez le planning des travaux pour la journée en cours.</p>
    </div>
  );
};

export default MeteoPlanning;
